**  04/28/2020   Windsor     Created.
**
***************************************************************************************************/ --
CREATE PROCEDURE [dbo].[CME_DELETE_DATA] @DeleteStaging bit = 1 AS
DECLARE
    @CE_SUBM_ID varchar(40);
DECLARE @getSubmId CURSOR;
BEGIN
    SET NOCOUNT ON;
    SET XACT_ABORT,
        QUOTED_IDENTIFIER,
        ANSI_NULLS,
        ANSI_PADDING,
        ANSI_WARNINGS,
        ARITHABORT,
        CONCAT_NULL_YIELDS_NULL ON;
    SET NUMERIC_ROUNDABORT OFF;

    DECLARE @int INT;

    DECLARE @localTran BIT
    IF @@TRANCOUNT = 0
        BEGIN
            SET @localTran = 1
            BEGIN TRANSACTION LocalTran
        END

    BEGIN TRY
        SET @getSubmId = CURSOR FOR
            SELECT CME_SUBM_DEL_ID
            FROM NODE_RCRA_CME_SUBM_DEL
            WHERE CME_SUBM_DEL_ID NOT IN (SELECT SUBM_ID FROM ETL_RUN WHERE SUBM_ID IS NOT NULL AND ETL_TYPE = 'CD');
        OPEN @getSubmId;
        FETCH NEXT FROM @getSubmId
            INTO @CE_SUBM_ID;
        WHILE @@FETCH_STATUS = 0
            BEGIN
                EXEC CME_DELETE_LOG @CE_SUBM_ID = @CE_SUBM_ID;
                EXEC CME_DELETE_VIOL @CE_SUBM_ID = @CE_SUBM_ID;
                EXEC CME_DELETE_ENFRC_ACT @CE_SUBM_ID = @CE_SUBM_ID;
                EXEC CME_DELETE_EVAL @CE_SUBM_ID = @CE_SUBM_ID;
                -- FIXME: here -- write these SPs

                IF @DeleteStaging = 1
                    BEGIN
                        DELETE
                        FROM NODE_RCRA_CME_SUBM_DEL
                        WHERE CURRENT OF @getSubmId;
                    END;
                FETCH NEXT FROM @getSubmId
                    INTO @CE_SUBM_ID;
            END;
        CLOSE @getSubmId;
        DEALLOCATE @getSubmId;
        IF @localTran = 1 AND XACT_STATE() = 1
            COMMIT TRAN LocalTran

    END TRY
    BEGIN CATCH

        DECLARE @ErrorMessage NVARCHAR(4000)
        DECLARE @ErrorSeverity INT
        DECLARE @ErrorState INT

        SELECT @ErrorMessage = ERROR_MESSAGE(),
               @ErrorSeverity = ERROR_SEVERITY(),
               @ErrorState = ERROR_STATE()

        IF @localTran = 1 AND XACT_STATE() <> 0
            ROLLBACK TRAN

        RAISERROR (@ErrorMessage, @ErrorSeverity, @ErrorState)

    END CATCH
END
